import React from 'react';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';


import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';


// import { connect } from 'react-redux';
// import { reduxSelector, reduxActionCreator } from '../../../redux/exampleRedux.js';

import styles from './ProductBox.module.scss';

const Component = ({ id, name, type, price, photo }) => (
  <Card className={styles.root}>
    <CardActionArea
      className={styles.cardClickableArea}
      component={NavLink}
      exact to={`product/${id}`}
    >
      <CardMedia
        component='img'
        alt={type}
        image={photo}
        className={styles.photo}
      />

      <Button className={styles.link} component={Link} to={`/cart`}>
          <ShoppingBasketIcon className={styles.icon} />
        </Button>

      <CardContent>
        <Typography
          gutterBottom
          variant='h5'
          component='h2'
          className={styles.cardTitle}
        >
          {name}
        </Typography>

        <Typography
          variant='body1'
          component='p'
          className={styles.cardBody}
        >
          Prices start from {price}$!
        </Typography>
      </CardContent>
    </CardActionArea>

    <CardActions>
      <Button
        variant='contained'
        size='large'
        color='secondary'
        component={NavLink}
        exact to={`/product/${id}`}
        className={styles.button}
      >
        Show more
      </Button>
    </CardActions>

  </Card>
);

Component.propTypes = {
  id: PropTypes.string,
  name: PropTypes.string,
  type: PropTypes.string,
  price: PropTypes.number,
  photo: PropTypes.node,
};

// const mapStateToProps = state => ({
//   someProp: reduxSelector(state),
// });

// const mapDispatchToProps = dispatch => ({
//   someAction: arg => dispatch(reduxActionCreator(arg)),
// });

// const Container = connect(mapStateToProps, mapDispatchToProps)(Component);

export {
  Component as ProductBox,
  // Container as ProductBox,
  Component as ProductBoxComponent,
};
